mapper.select:
select count(*)+1
from course_member
where cno=#{cno}

mapper.update:
update course
set snum=snum+1
where cno=#{cno}

update course
set snum=snum-1
where cno=#{cno}

update course_member
set status=#{status}
where cno=#{cno} and member=#{uid}

update course_member
set status=#{status}
where cno=#{cno}
